// src/Components/AddUser/AddUser.js
import React, { UseState } from 'react';
import { UseNavigate } from 'react-router';
import './userStyles.css';
import NavBar from '../NavBar/navBar'; // Ensure NavBar is imported correctly
import axios from 'axios';

function AddUser() {
    const history = UseNavigate(); 
    const [inputs, setInputs] = UseState({
        name: '',
        age: '',
        address: '',
    });

    const handleChange = (e) => {
        setInputs((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Add User Form Submitted:', inputs);
        sendRequest().then(() => history('viewDetails')); // Redirect to home after submission
    }

    const sendRequest = async () => {
        await axios.post('http://localhost:5000/users', {
            name: inputs.name,
            age: inputs.age,
            address: inputs.address,
        });
    }


    return (
        <div className="user-container">
        <NavBar />
            <div className="form-container">
                <h2>Add User</h2>
                <form onSubmit>
                    <input name="name" type="text" placeholder="Name" value={inputs.name} required />
                    <input name="age" type="number" placeholder="Age" value={inputs.age} required />
                    <input name="address" type="text" placeholder="Address" value={inputs.address} required />
                    <button type="submit">Submit</button>
                </form>
            </div>
        </div>

    );
}

export default AddUser;
